import React, { Component } from "react";
import "./App.css";

class App extends Component {
  state = {
    question: null,
    a: null,
    b: null,
    c: null
  };

  componentDidMount() {
    this.callApi()
      .then(res => {
        this.setState({
          question: res.question,
          a: [res.a, res.a === res.answer],
          b: [res.b, res.b === res.answer],
          c: [res.c, res.c === res.answer]
        });
        console.log(res);
      })
      .catch(err => console.log(err));
  }

  callApi = async () => {
    const response = await fetch("/api/hello");
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);
    return body;
  };

  // handleSubmit = async e => {
  //   e.preventDefault();
  //   const response = await fetch("/api/world", {
  //     method: "POST",
  //     headers: {
  //       "Content-Type": "application/json"
  //     },
  //     body: JSON.stringify({ post: this.state.post })
  //   });
  //   const body = await response.text();
  //   this.setState({ responseToPost: body });
  // };

  render() {
    return (
      <div className="app">
        {this.state.question && <h2>{this.state.question}</h2>}
        {this.state.a && <button>{this.state.a[0]}</button>}
        {this.state.b && <button>{this.state.b[0]}</button>}
        {this.state.c && <button>{this.state.c[0]}</button>}
      </div>
    );
  }
}
export default App;
